import classes from './Posts.module.css';
import Post from './post/Post'
import React from 'react';

const Posts = (props) => { 
   let posts = props.posts.map(post => <Post key={post.id} id={post.id} text = {post.text} likes = {post.likes} dispatch={props.dispatch}/>);


   let addPost = (e) => {
       let action = { type:"ADD_POST"}
       props.dispatch(action)
   }

   let textChanged = (e) =>{
      let action = { type:"CHANGE_NEW_POST_TEXT", text: e.target.value}
      props.dispatch(action);
   }

    return ( 
       <div className={classes.posts}>
          <h1>My Posts</h1> 
          <input  type="text" className={classes.text}  value={props.newPostText} onChange={textChanged}placeholder='your news...'/>
          <input type="button" className={classes.btn} value="Send" onClick={addPost}/>
          <div className={classes.po}>
            {posts}
          </div>
       </div>
       
    );
}

export default Posts;

