import classes from './Posts.module.css';
import Post from './post/Post'
import React from 'react';

const Posts = (props) => { 
   let posts = props.posts.map(post => <Post key={post.id} id={id.post} text = {post.text} likes = {post.likes}/>);

   let textRef = React.createRef();


   let addPost = () => {
      let text = textRef.current.value;
      props.addPost(text);
   }

    return ( 
       <div className={classes.posts}>
          <h1>My Posts</h1> 
          <input ref={textRef} type="text" className={classes.text} placeholder='your news...'/>
          <input type="button" className={classes.btn} value="Send" onClick={addPost}/>
          <div className={classes.po}>
            {posts}
          </div>
       </div>
       
    );
}

export default Posts;

