import classes from './Messages.module.css'
import { newMsgTextActionCreator, addMsgActionCreator } from '../../../redux/dialogReducer';

const Messages = (props) => {

    let msg = props.messages.map(m => <li>{m.content} || {m.author}</li>)

    let onTextChange = (e) => {
        let action = newMsgTextActionCreator(e.target.value);
        props.dispatch(action);
    }

    let onSendClick = () => {
        let action = addMsgActionCreator();
        props.dispatch(action);
    }

    return (
        <div>
            <div className={classes.con}></div>
            <ul>
                {msg}
            </ul>
            <div className={classes.inputs}>
            <input className={classes.text} type="text" value={props.newMsgText} onChange={onTextChange}/>
            <button className={classes.btn} onClick={onSendClick}>Send</button>
            </div>
        </div>
    )
}

export default Messages;
