const state = {
    posts: [
            {id:1, text:"Hello, my name is Vlad"},
            {id:2, text:")))"}
    ]
}

export const addPost = (postText) =>{
  let post = {
      id: state.posts.length,
      text: postText
  }
  state.posts.push(post)
}

export default state;
